		<style>
			body {
				margin: 0;
			}
			.GenModul {
				position: relative;
				margin-left: 25vw;
				margin-top: 7vh;
				border-radius: 15px;
			}
			.header {
				position: absolute;
				top: 0;
				width: 200px;
				height: 87vh;
				max-height: 546px;
				background-color: rgba(0, 0, 0, 0.8);
				color: #fff;
				padding-left: 7px;
				font-family: 'Cuprum', sans-serif;
			    border-radius: 12px 0 0 12px;
			}
			.header h1 {
				font-size: 46px;
			}
			.header span {
				font-size: 36px;
				font-weight: 700;
			}
			.header span.blue {
				color: #007eff;			
			}
			.header span.brown {
				color: #ff9900;			
			}
			.header div#temp {
				color: #fff;
				font-size: 44px;
				text-align: right;
				padding-right: 50px;
			}
			.header div span {
				color: #fff;
			}
			.header div {
				color: #fff;
			}
			.header div#windsp span.left {
				padding-right: 69px;				
				font-size: 28px;
			}
			.header div#humd span.left {
				padding-right: 27px;				
				font-size: 28px;
			}
			.header div#press span.left {
				padding-right: 48px;				
				font-size: 28px;
			}
			.header div span.left {				
				font-size: 28px;
			}
			.header div span.right {				
				font-size: 26px;
			}
			.header div span.small {
				font-size: 24px;
			}
			.footer {							
				position: absolute;
				top: 405px;
				height: 200px;
				width: 795px;
				background-image: linear-gradient(to right,#3b454b, #1e2831);
				border-radius: 0 0 12px 12px;
			}
			.GenModul img {
			   border-radius: 12px;
			}
			ul {
			  display: flex;
			  list-style-type: none;
			  margin: 0;
			  padding: 0;
			}
			ul li {
			    width: 100px;
			}
			ul li div:first-child {
				width: 58px;				
				margin-top: 7px;
				margin-left: 20px;
				padding: 1px 0;
				border: 1px black solid;
				border-radius: 19px;
				font-weight: 900;
				font-size: 21px;
				font-family: 'Cuprum', sans-serif;
				background-color: #957851;
				text-align: center;
			}
			ul li > div:nth-child(2) {				
				color: #fff;
				font-size: 40px;
				font-family: 'Cuprum', sans-serif;
				margin-top: 7px;
				margin-left: 21px;
			}
			ul li > div:nth-child(3) {				
				color: #fff;
				font-size: 30px;
				font-family: 'Cuprum', sans-serif;
				margin-top: 7px;
				margin-left: 25px;
			}
			ul li > div:nth-child(4) {				
				color: #fff;
				font-size: 23px;
				font-family: 'Cuprum', sans-serif;
				margin-top: 7px;
				margin-left: 17px;
			}
